import { LoginHeader } from "../LoginPage/LoginHeader"
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from 'axios';
//import album_cover from './speak_now.png'
import '../LoginPage/LoginPage.css'
import './AlbumPage.css'

function AlbumInfo() {
    const params = useParams();
    const [tracks, setTracks] = useState([]);
    const [albumName, setAlbumName] = useState('');
    const [coverArtUrl, setCoverArtUrl] = useState('');

    useEffect(() => {
        axios.get('http://localhost:8080/albums?name='+params.name)
            .then((response) => {
                setTracks(response.data.tracks);
                setAlbumName(response.data.name);
                setCoverArtUrl(response.data.coverArtUrl);
            })
            .catch((error) => {
                console.error(error);
            });
    }, []);


    return (
        <div className="albumPlaceHolder" >
            <h1>Album Page</h1>
            <img alt='Cover Art' src={coverArtUrl} ></img>
            <p>{albumName}</p>

            <h2>Tracks:</h2>
            <ol>
                {tracks.map((track, idx) => (
                    <li key={idx}>{track}</li>
                ))}
            </ol>
        </div>
    )
}



function AlbumPage() {
    return (
        <div className="albumPage">
            <LoginHeader />
            <AlbumInfo />
        </div>
    )
}

export default AlbumPage;
