import {React, useState} from 'react'
import axios from 'axios'

function SearchResults({data}) {
    return (
        <div className="searchResults">
            <ul>
                {data.map((item, idx) => (
                    <li key={idx}>{item}</li>
                ))}
            </ul>
        </div>
    );
}



export function SearchBox() {
    const [searchResults, setSearchResults] = useState([]);
    const [albumName, SetAlbumName] = useState('');

    const handleInputChange = (event) => {
        SetAlbumName(event.target.value);
    }

    const getSearchResults = () => {
        if (!albumName) {
            return;
        }

        axios.get('http://localhost:8080/getAlbumSearchResults?name='+albumName)
        .then((response) => {
            setSearchResults(response.data);
        })
        .catch((error => {
            console.error(error);
        }))
    }


    return (
        <div className="searchBox">
            <p> Search Album Here: </p>
            <input type='text' value='Search...' onChange={handleInputChange}></input>
            <button className='searchButton' onClick={getSearchResults}> Find Album </button>
            <SearchResults data={searchResults} />
        </div>
    );
}
