import {React, useState} from 'react'
import axios from 'axios'

function SearchResults({data}) {
    return (
        <div className="searchResults">
            <ul>
                {data.map((item, idx) => (
                    <li key={idx}>{item}</li>
                ))}
            </ul>
        </div>
    );
}



