import { useState } from "react"
import axios from "axios";

export function ReviewBox ({ albumName }) {
    const [review, setReview] = useState('');

    const handleReviewChange = (event) => {
        setReview(event.target.value);
    } 

    const handleSaveReview = () => {
        if (!review) {
            return;
        }

        const reviewData = { 
            reviewText: review,
            albumName: albumName
        };

        axios.post('localhost:8080/submitReview', reviewData)
            .then((response) => {
                window.alert('Review saved!');
            })
            .catch((error) => {
                console.error('Error saving: ', error);
            })
    };


    return (
        <div className="reviewBox">
            <h2>Write a Review: </h2>
            <textarea value={review} onChange={handleReviewChange}></textarea>
            <button onClick={handleSaveReview}>Save Review </button>
        </div>
    )
}
