import { LoginHeader } from "../LoginPage/LoginHeader"
import { useParams } from "react-router-dom";
import { ReviewBox } from './ReviewBox';
import { useState, useEffect } from "react";
import axios from 'axios';
//import album_cover from './speak_now.png'
import '../LoginPage/LoginPage.css'
import './AlbumPage.css'

function AlbumInfo() {
    const params = useParams();
    const [tracks, setTracks] = useState([]);
    const [albumName, setAlbumName] = useState('');
    const [coverArtUrl, setCoverArtUrl] = useState('');
    const [artistName, setArtistName] = useState('');
    const [review, setReview] = useState('');


    useEffect(() => {
        axios.get('http://localhost:8080/findReview?albumName='+params.name)
        .then((response) => {
            if (response.data === true) {
                window.alert("Review already added");
            }
            else {
                window.alert("No review added yet");
            }
        })
        .catch((error) => {
            console.error(error);
        });
    }, [params.name]);

    useEffect(() => {
        axios.get('http://localhost:8080/albums?name='+params.name)
            .then((response) => {
                setTracks(response.data.tracks);
                setAlbumName(response.data.name);
                setCoverArtUrl(response.data.coverArtUrl);
                setArtistName(response.data.artist);
            })
            .catch((error) => {
                console.error(error);
            });
    }, [params.name]);

    const handleReviewChange = (event) => {
        setReview(event.target.value);
    } 

    const handleSaveReview = () => {
        if (!review) {
            return;
        }

        const reviewData = { 
            reviewText: review,
            albumName: albumName
        };

        axios.post('http://localhost:8080/submitReview', reviewData)
            .then((response) => {
                window.alert('Review saved!');
            })
            .catch((error) => {
                console.error('Error saving: ', error);
            })
    };


    return (
        <div className="albumWrapper" >
            <div className="albumInfo" >
                <div className="album">
                    <h2 id="album-name">{albumName}</h2>
                    <h2 id="artist-name">{artistName}</h2>
                    <img id="cover-art" alt='Cover Art' src={coverArtUrl} ></img>
                </div>
                <div className="tracks-info">
                    <h2 id="tracks">Tracks:</h2>
                    <ol id="track-list">
                        {tracks.map((track, idx) => (
                            <li key={idx}>{track}</li>
                        ))}
                    </ol>
                </div>

            </div>
            < ReviewBox 
                    review={review}
                    onReviewChange={handleReviewChange}
                    onSaveReview={handleSaveReview}
            />
        </div>
    )
}



function AlbumPage() {
    return (
        <div className="albumPage">
            <LoginHeader />
            <AlbumInfo />
        </div>
    )
}

export default AlbumPage;
